const ROOT_PATH = '/../../';

var fs = require('fs'),
    path = require('path'),
    fileCache = {};

exports.load = function(filename) {
    if (!fileCache[filename]) {
        var file = path.resolve(__dirname + ROOT_PATH, filename);

        fileCache[filename] = fs.readFileSync(file, 'UTF-8');
    }

    return fileCache[filename];
}